mapper.select:
SELECT m.id as memberId, m.member_name as memberName, c.id, c.title, c.register_time as registerTime FROM community c, member m WHERE c.member_id = m.id
and m.id in
#{id}
and c.title like CONCAT('%',#{searchTitle},'%')
order by c.id desc LIMIT #{page},#{size}

SELECT wikiId AS id, title AS title FROM ( SELECT b.wiki_id AS wikiId, b.his_yn, w.title, b.member_id FROM wiki_his b, wiki w WHERE w.id = b.wiki_id
and b.title like CONCAT('%',#{searchTitle},'%')
and b.member_id in
#{id}
) wiki GROUP BY wikiId, title

SELECT count(1) as cnt FROM ( SELECT b.wiki_id AS wikiId, b.his_yn, w.title, b.member_id FROM wiki_his b, wiki w WHERE w.id = b.wiki_id
and b.title like CONCAT('%',#{searchTitle},'%')
and b.member_id in
#{id}
) wiki
