mapper.select:
SELECT m.id as memberId, m.member_name as memberName, m.picture, c.id, c.title, u.id as menuId, u.name as menuName, c.register_time as registerTime, (select count(1) from com_comment where community_id = c.id) as commentCount, (select count(1) from com_like where community_id = c.id) as likesCount, (select count(1) from com_hits where community_id = c.id) as hitsCount FROM community c, member m, menu u WHERE c.member_id = m.id and u.id = c.menu_id
and c.menu_id = #{menuId}
and c.member_id = #{memberId}
order by c.id desc LIMIT #{page},#{size}

SELECT count(1) cnt FROM community c, member m, menu u WHERE c.member_id = m.id and u.id = c.menu_id
and c.menu_id = #{menuId}
and c.member_id = #{memberId}

SELECT m.id as memberId, m.member_name as memberName, m.picture, c.id, c.title, u.id as menuId, u.name as menuName, c.register_time as registerTime FROM community c, member m, menu u WHERE c.member_id = m.id and u.id = c.menu_id
and c.menu_id = #{menuId}
order by c.id desc LIMIT #{size}
